const datosPacientes = [10, 20, 15, 30, 25]; // Cantidad de pacientes atendidos por cada doctor

const ctx = document.getElementById('graficoBarras').getContext('2d');
new Chart(ctx, {
type: 'bar',
data: {
  labels: ['Doctor 1', 'Doctor 2', 'Doctor 3', 'Doctor 4', 'Doctor 5'],
  datasets: [{
    label: 'Pacientes atendidos',
    data: datosPacientes,
    backgroundColor: 'rgba(54, 162, 235, 0.6)', // Color de las barras
    borderColor: 'rgba(54, 162, 235, 1)', // Color del borde de las barras
    borderWidth: 1 // Grosor del borde
  }]
},
options: {
  scales: {
    y: {
      beginAtZero: true // Comienza el eje y desde cero
    }
  }
}
});